import './App.css';
import { useState } from "react"; 
import { Label, Note } from "./types"; // Import the Label type from the appropriate module
import { dummyNotesList } from "./constants"; // Import the dummyNotesList from the appropriate module
import ToggleTheme, { ClickCounter } from './hooksExercise';
import { likeButtonThemes } from './buttonThemeContext'; 
import { LikeButton } from './LikeButton';
import heartFull from './heartFull.png';
import heartEmpty from './heartEmpty.png';


function App() {

  const [favoriteList, setFavoriteList] = useState([])
  const [likeState, changeLikeState] = useState(false);

  const onButtonClick = (title: any) => {

      // check if liked or unliked

      // if unliked: 
          // change button to liked
          // add title to list
      // if liked: 
          // chnage button to unliked
          // remove title from list


      if (likeState == false && !favoriteList.includes(title)) {
          favoriteList.push(title);
          setFavoriteList(favoriteList)

          console.log(favoriteList)

      } else if (likeState == true && favoriteList.includes(title)) {
          const index = favoriteList.indexOf(title);
          favoriteList.splice(index, 1);
          setFavoriteList(favoriteList);
          console.log(favoriteList)

      }
      changeLikeState(!likeState);
  }


return (
  <div className='app-container'>
    <ToggleTheme />
    <form className="note-form">
      <div><input placeholder="Note Title"></input></div>

      <div><textarea></textarea></div>

      <div><button type="submit">Create Note</button></div>
    </form>
    <div className="notes-grid">
      {dummyNotesList.map((note) => (
        <div
          key={note.id}
          className="note-item">
          <div className="notes-header">
            {/* <LikeButton title={note.title} favList={favoriteList} setFavList={setFavoriteList}/> */}
            <div>
                <button onClick={() => onButtonClick(note.title)}>
                    { likeState ? <img className="heart-button" src={heartFull} /> : <img className="heart-button" src={heartEmpty} />}
                </button>
            </div>

            <button>x</button>
          </div>
          <h2> {note.title} </h2>
          <p> {note.content} </p>
          <p> {note.label} </p>
        </div>
      ))}
      <h2>List of favorites:</h2>
      <div>
        {favoriteList.map(txt => <p>{txt}</p>)}
      </div>

    </div>

  </div>

);
}

export default App;
